function minusSeven(num) {
    return num - 7;
}

console.log(minusSeven(10)); // 3

// Storing returned value
var result = minusSeven(20);
console.log(result); // 13

// Multiple returns (only first executes)
function checkNumber(num) {
    if (num > 10) {
        return "Big number";
    }
    return "Small number";
}

console.log(checkNumber(15)); // "Big number"
console.log(checkNumber(5));  // "Small number"

// Return stops execution
function testReturn() {
    console.log("Before return");
    return "Returned value";
    console.log("After return"); // Never executes
}

console.log(testReturn());
// Output:
// Before return
// Returned value

