console.log("cardCount('K'):", cardCount('K')); // "0 Hold"
console.log("cardCount(10):", cardCount(10)); // "-1 Hold"
console.log("cardCount('K'):", cardCount('K')); // "-2 Hold"
console.log("cardCount('A'):", cardCount('A')); // "-3 Hold"
console.log("cardCount(4):", cardCount(4)); // "-2 Hold"

// Reset counter for new game
