// Advanced card counter with more features
var advancedCount = 0;
var trueCount = 0;
var decksRemaining = 6;

function advancedCardCount(card) {
    // Update running count
    switch (card) {
        case 2: case 3: case 4: case 5: case 6:
            advancedCount++;
            break;
        case 10: case "J": case "Q": case "K": case "A":
            advancedCount--;
            break;
        case 7: case 8: case 9:
            // Neutral cards, no change
            break;
        default:
            return "Invalid card";
    }

    // Calculate true count
    trueCount = Math.round(advancedCount / decksRemaining);

    // Determine betting strategy
    var betAmount = 1; // Base bet
    if (trueCount >= 2) {
        betAmount = trueCount * 2;
    }

    return {
        runningCount: advancedCount,
        trueCount: trueCount,
        betAmount: betAmount,
        strategy: trueCount > 0 ? "Bet" : "Hold"
    };
}

// Test advanced counter
resetCounter();
advancedCount = 0;

