// Card counter with deck tracking
var deckCount = 0;
var totalCards = 52;

function deckCardCount(card) {
    deckCount++;

    // Update count based on card
    switch (card) {
        case 2: case 3: case 4: case 5: case 6:
            count++;
            break;
        case 10: case "J": case "Q": case "K": case "A":
            count--;
            break;
    }

    // Calculate cards remaining
    var cardsRemaining = totalCards - deckCount;
    var decksRemaining = cardsRemaining / 52;

    // Calculate true count
    var trueCount = Math.round(count / decksRemaining);

    return {
        card: card,
        runningCount: count,
        trueCount: trueCount,
        cardsRemaining: cardsRemaining,
        decksRemaining: Math.round(decksRemaining * 10) / 10
    };
}

