const nums = [1, 2, 3, 4, 5];
const [a, b, c] = nums;

console.log("A:", a); // 1
console.log("B:", b); // 2
console.log("C:", c); // 3

// Destructuring with different variable names
const { name: fullName, age: years } = person;
console.log("Full name:", fullName); // "Ali"
console.log("Years:", years);       // 15

// Destructuring with default values
const { name: userName, country = "USA" } = person;
console.log("User name:", userName); // "Ali"
console.log("Country:", country);    // "USA" (default)

// Destructuring in function parameters
function greet({ name, city }) {
    return `Hello, ${name} from ${city}!`;
}

const person2 = {
    name: "Aria",
    age: 20,
    city: "Nairobi"
};

console.log("Greeting:", greet(person2)); // "Hello, Aria from Nairobi!"

// Skipping elements in array destructuring
const [first, , third] = nums;
console.log("First:", first); // 1
console.log("Third:", third); // 3

// Rest operator with destructuring
const [firstNum, ...rest] = nums;
console.log("First number:", firstNum); // 1
console.log("Rest:", rest);            // [2, 3, 4, 5]

// Destructuring nested objects
const user = {
    name: "John",
    address: {
        street: "123 Main St",
        city: "Boston",
        state: "MA"
    },
    preferences: {
        theme: "dark",
        notifications: true
    }
};

