console.log("Person:", personObj);  // { name: "John" }

// Destructuring with error handling
function safeDestructure(obj) {
    try {
        const { name, age } = obj;
        return { name, age };
    } catch (error) {
        return { name: "Unknown", age: 0 };
    }
}

console.log("Safe destructure:", safeDestructure(person));
console.log("Safe destructure (null):", safeDestructure(null));

