// Rectangle class with computed properties
class Rectangle {
    constructor(width, height) {
        this._width = width;
        this._height = height;
    }

    get width() {
        return this._width;
    }

    set width(value) {
        if (value <= 0) {
            throw new Error("Width must be positive");
        }
        this._width = value;
    }

    get height() {
        return this._height;
    }

    set height(value) {
        if (value <= 0) {
            throw new Error("Height must be positive");
        }
        this._height = value;
    }

    get area() {
        return this._width * this._height;
    }

    get perimeter() {
        return 2 * (this._width + this._height);
    }

    get isSquare() {
        return this._width === this._height;
    }
}

