// Password strength checker
function checkPasswordStrength(password) {
    const hasUpper = /[A-Z]/.test(password);
    const hasLower = /[a-z]/.test(password);
    const hasNumber = /\d/.test(password);
    const hasSpecial = /[!@#$%^&*(),.?":{}|<>]/.test(password);

    let strength = 0;
    let feedback = [];

    if (hasUpper) {
        strength++;
        feedback.push("✓ Has uppercase letters");
    } else {
        feedback.push("✗ Needs uppercase letters");
    }

    if (hasLower) {
        strength++;
        feedback.push("✓ Has lowercase letters");
    } else {
        feedback.push("✗ Needs lowercase letters");
    }

    if (hasNumber) {
        strength++;
        feedback.push("✓ Has numbers");
    } else {
        feedback.push("✗ Needs numbers");
    }

    if (hasSpecial) {
        strength++;
        feedback.push("✓ Has special characters");
    } else {
        feedback.push("✗ Needs special characters");
    }

    const strengthLevels = ["Very Weak", "Weak", "Medium", "Strong", "Very Strong"];

    return {
        strength: strengthLevels[strength],
        score: strength,
        feedback: feedback
    };
}

console.log("\n=== PASSWORD STRENGTH CHECKER ===");
const passwords = ["password", "Password", "Password123", "Password123!", "P@ssw0rd!"];
passwords.forEach(password => {
    const result = checkPasswordStrength(password);
    console.log(`Password: "${password}"`);
    console.log(`Strength: ${result.strength} (${result.score}/4)`);
    result.feedback.forEach(feedback => console.log(`  ${feedback}`));
    console.log();
});

// Text analysis
function analyzeText(text) {
    const words = text.toLowerCase().split(/\s+/);
    const wordCount = words.length;
    const charCount = text.length;
    const upperCount = (text.match(/[A-Z]/g) || []).length;
    const lowerCount = (text.match(/[a-z]/g) || []).length;

    return {
        wordCount,
        charCount,
        upperCount,
        lowerCount,
        upperPercentage: ((upperCount / charCount) * 100).toFixed(1),
        lowerPercentage: ((lowerCount / charCount) * 100).toFixed(1)
    };
}

