    console.log(`Text: "${text}"`);
    console.log(`  Words: ${analysis.wordCount}`);
    console.log(`  Characters: ${analysis.charCount}`);
    console.log(`  Uppercase: ${analysis.upperCount} (${analysis.upperPercentage}%)`);
    console.log(`  Lowercase: ${analysis.lowerCount} (${analysis.lowerPercentage}%)`);
    console.log();
});

