// Find first non-repeating character
function findFirstNonRepeating(str) {
    const frequency = getCharacterFrequency(str);

    for (let i = 0; i < str.length; i++) {
        const char = str.charAt(i);
        if (frequency[char] === 1) {
            return char;
        }
    }
    return null;
}

