// Create a container to hold all elements
const container = document.createElement('div');
container.className = 'dynamic-content';

// Add elements to container
container.append(heading, paragraph, button, input, image);

// Add container to page
if (bigBox) {
    bigBox.append(container);
}

console.log("Created elements:", container.children);

// Advanced content creation

// Create element with attributes
function createElementWithAttributes(tagName, attributes, content) {
    const element = document.createElement(tagName);

    // Set attributes
    Object.entries(attributes).forEach(([key, value]) => {
        element.setAttribute(key, value);
    });

    // Set content
    if (content) {
        if (typeof content === 'string') {
            element.textContent = content;
        } else if (content.innerHTML) {
            element.innerHTML = content.innerHTML;
        }
    }

    return element;
}

// Example usage
const styledDiv = createElementWithAttributes('div', {
    'class': 'styled-element',
    'id': 'unique-id',
    'data-custom': 'custom-value'
}, 'Styled content');

console.log("\n=== CREATED ELEMENT WITH ATTRIBUTES ===");
console.log("Element:", styledDiv);
console.log("Classes:", styledDiv.className);
console.log("ID:", styledDiv.id);
console.log("Custom attribute:", styledDiv.getAttribute('data-custom'));

// Create element with HTML content
function createElementWithHTML(tagName, htmlContent, attributes = {}) {
    const element = document.createElement(tagName);
    element.innerHTML = htmlContent;

    Object.entries(attributes).forEach(([key, value]) => {
        element.setAttribute(key, value);
    });

    return element;
}

// Example with HTML content
const htmlElement = createElementWithHTML('div', 
    '<h3>Title</h3><p>Paragraph with <strong>bold</strong> text</p>',
    { 'class': 'html-content' }
);

console.log("\n=== CREATED ELEMENT WITH HTML ===");
console.log("HTML content:", htmlElement.innerHTML);

// Different ways to add content

// Method 1: append() - adds at the end
function addWithAppend(parent, element) {
    parent.append(element);
}

// Method 2: prepend() - adds at the beginning
function addWithPrepend(parent, element) {
    parent.prepend(element);
}

// Method 3: appendChild() - older method, adds at the end
function addWithAppendChild(parent, element) {
    parent.appendChild(element);
}

// Method 4: insertBefore() - inserts before a specific element
function addWithInsertBefore(parent, newElement, referenceElement) {
    parent.insertBefore(newElement, referenceElement);
}

// Method 5: insertAdjacentElement() - more flexible positioning
function addWithInsertAdjacent(parent, element, position) {
    parent.insertAdjacentElement(position, element);
}

// Demonstration of different methods
const demoContainer = document.createElement('div');
demoContainer.className = 'demo-container';

// Add reference element
const referenceElement = document.createElement('div');
referenceElement.textContent = 'Reference element';
demoContainer.append(referenceElement);

// Add elements using different methods
const element1 = document.createElement('div');
element1.textContent = 'Added with append';
element1.className = 'method-1';
addWithAppend(demoContainer, element1);

const element2 = document.createElement('div');
element2.textContent = 'Added with prepend';
element2.className = 'method-2';
addWithPrepend(demoContainer, element2);

const element3 = document.createElement('div');
element3.textContent = 'Added with insertBefore';
element3.className = 'method-3';
addWithInsertBefore(demoContainer, element3, referenceElement);

const element4 = document.createElement('div');
element4.textContent = 'Added with insertAdjacent';
element4.className = 'method-4';
addWithInsertAdjacent(demoContainer, element4, 'beforeend');

console.log("\n=== DIFFERENT ADDITION METHODS ===");
console.log("Container children:", Array.from(demoContainer.children).map(el => el.textContent));

// Batch content creation
function createBatchContent(items, containerClass = 'batch-container') {
    const container = document.createElement('div');
    container.className = containerClass;

    items.forEach((item, index) => {
        const element = document.createElement('div');
        element.textContent = item;
        element.className = `item-${index}`;
        container.append(element);
    });

    return container;
}

// Example batch creation
const items = ['Item 1', 'Item 2', 'Item 3', 'Item 4'];
const batchContainer = createBatchContent(items);

console.log("\n=== BATCH CONTENT CREATION ===");
console.log("Batch container:", batchContainer);
console.log("Number of items:", batchContainer.children.length);

// Content with event listeners
function createInteractiveElement(tagName, content, eventType, handler) {
    const element = document.createElement(tagName);
    element.textContent = content;
    element.addEventListener(eventType, handler);
    return element;
}

// Example interactive element
const interactiveButton = createInteractiveElement('button', 'Click me!', 'click', function() {
    console.log('Button clicked!');
    this.textContent = 'Clicked!';
});

console.log("\n=== INTERACTIVE ELEMENT ===");
console.log("Interactive button:", interactiveButton);

// Template-based content creation
function createFromTemplate(template) {
    const element = document.createElement('div');
    element.innerHTML = template;
    return element.firstElementChild;
}

// Example template
const template = `
    <article class="article">
        <header>
            <h2>Article Title</h2>
        </header>
        <main>
            <p>Article content goes here...</p>
        </main>
        <footer>
            <small>Published on ${new Date().toLocaleDateString()}</small>
        </footer>
    </article>
`;

const articleElement = createFromTemplate(template);

console.log("\n=== TEMPLATE-BASED CREATION ===");
console.log("Article element:", articleElement);
console.log("Article HTML:", articleElement.outerHTML);

// Performance considerations
function performanceTest() {
    console.log("\n=== PERFORMANCE TEST ===");

    const testContainer = document.createElement('div');

    // Test 1: Individual append operations
    console.time('Individual appends');
    for (let i = 0; i < 1000; i++) {
        const element = document.createElement('div');
        element.textContent = `Item ${i}`;
        testContainer.append(element);
    }
    console.timeEnd('Individual appends');

    // Test 2: Document fragment (faster)
    const fragment = document.createDocumentFragment();
    console.time('Document fragment');
    for (let i = 0; i < 1000; i++) {
        const element = document.createElement('div');
        element.textContent = `Item ${i}`;
        fragment.append(element);
    }
    testContainer.append(fragment);
    console.timeEnd('Document fragment');

    // Clean up
    testContainer.remove();
}

performanceTest();

// Best practices
console.log("\n=== BEST PRACTICES ===");
console.log("1. Use createElement() for new elements");
console.log("2. Use textContent for plain text (safer than innerHTML)");
console.log("3. Use append() instead of appendChild() (modern)");
console.log("4. Use document fragments for multiple elements");
console.log("5. Set attributes after creating the element");
console.log("6. Use insertAdjacentElement() for flexible positioning");
console.log("7. Clean up event listeners when removing elements");

// Common patterns
console.log("\n=== COMMON PATTERNS ===");
console.log("1. Create: const el = document.createElement('div')");
console.log("2. Configure: el.textContent = 'content'; el.className = 'class'");
console.log("3. Add: parent.append(el)");
console.log("4. Batch: fragment.append(el1, el2, el3); parent.append(fragment)");
console.log("5. Template: element.innerHTML = template; return element.firstElementChild");

// Clean up demo elements
if (container && container.parentNode) container.remove();
if (demoContainer && demoContainer.parentNode) demoContainer.remove();
if (batchContainer && batchContainer.parentNode) batchContainer.remove();
if (articleElement && articleElement.parentNode) articleElement.remove();

