console.log("While loop result:", myArr);

// Alternative with push
var myArr2 = [];
let j = 0;
while(j <= 10){
    myArr2.push(j); // Adds to end: [0, 1, 2, ...]
    j++;
}
console.log("While loop with push:", myArr2);

// For loop example
