for (var l = originalArray.length - 1; l >= 0; l--){
    reversedArray.push(originalArray[l]);
}
console.log("Original:", originalArray);
console.log("Reversed:", reversedArray);

// Practical example: countdown timer
function countdownTimer(seconds) {
    var timer = [];
    for (var m = seconds; m >= 0; m--) {
        timer.push(m);
    }
